pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/ICollateralManager.sol";
import "./interfaces/IPriceFeedAggregator.sol";
import "./base/CreditProtocolBase.sol";

/**
 * @title CollateralManager
 * @dev Manages multi-token collateral with dynamic pricing and liquidation
 */
contract CollateralManager is CreditProtocolBase, ICollateralManager {
    using SafeERC20 for IERC20;

    struct CollateralInfo {
        address token;
        uint256 amount;
        uint256 lockedAmount;
        uint256 lastUpdateTime;
    }

    mapping(address => mapping(address => CollateralInfo)) public userCollateral;
    mapping(address => bool) public supportedCollateral;
    mapping(address => uint256) public collateralFactors; // LTV ratios (basis points)
    mapping(address => uint256) public liquidationThresholds;

    IPriceFeedAggregator public priceFeed;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant LIQUIDATION_BONUS = 500; // 5% bonus

    address[] private supportedTokensList;

    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);
    event CollateralLocked(address indexed user, address indexed token, uint256 amount);
    event CollateralReleased(address indexed user, address indexed token, uint256 amount);
    event CollateralLiquidated(address indexed borrower, address indexed token, uint256 amount, address liquidator);
    event CollateralSwapped(address indexed user, address fromToken, address toToken, uint256 amountIn, uint256 amountOut);

    constructor(address _priceFeed, address _owner) CreditProtocolBase(_owner) {
        priceFeed = IPriceFeedAggregator(_priceFeed);
    }

    function depositCollateral(address token, uint256 amount) external override nonReentrant whenNotPaused {
        require(supportedCollateral[token], "Unsupported collateral");
        require(amount > 0, "Invalid amount");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        CollateralInfo storage info = userCollateral[msg.sender][token];
        info.token = token;
        info.amount += amount;
        info.lastUpdateTime = block.timestamp;

        emit CollateralDeposited(msg.sender, token, amount);
    }

    function withdrawCollateral(address token, uint256 amount) external override nonReentrant whenNotPaused {
        CollateralInfo storage info = userCollateral[msg.sender][token];
        require(info.amount >= amount, "Insufficient balance");
        require(info.amount - info.lockedAmount >= amount, "Amount locked");

        info.amount -= amount;
        info.lastUpdateTime = block.timestamp;

        IERC20(token).safeTransfer(msg.sender, amount);

        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    function lockCollateral(address user, address token, uint256 amount) external override onlyAuthorized {
        CollateralInfo storage info = userCollateral[user][token];
        require(info.amount >= info.lockedAmount + amount, "Insufficient collateral");

        info.lockedAmount += amount;
        info.lastUpdateTime = block.timestamp;

        emit CollateralLocked(user, token, amount);
    }

    function releaseCollateral(address user, address token, uint256 amount) external override onlyAuthorized {
        CollateralInfo storage info = userCollateral[user][token];
        require(info.lockedAmount >= amount, "Invalid release amount");

        info.lockedAmount -= amount;
        info.lastUpdateTime = block.timestamp;

        emit CollateralReleased(user, token, amount);
    }

    function getCollateralValue(address user) external view override returns (uint256 totalValue) {
        for (uint256 i = 0; i < supportedTokensList.length; i++) {
            address token = supportedTokensList[i];
            CollateralInfo memory info = userCollateral[user][token];

            if (info.amount > 0) {
                uint256 price = priceFeed.getPrice(token);
                uint256 decimals = IERC20Metadata(token).decimals();
                uint256 tokenValue = (info.amount * price) / (10 ** decimals);
                totalValue += tokenValue;
            }
        }
    }

    function getAvailableCollateral(address user, address token) external view override returns (uint256) {
        CollateralInfo memory info = userCollateral[user][token];
        return info.amount - info.lockedAmount;
    }

    function getLockedCollateral(address user, address token) external view returns (uint256) {
        return userCollateral[user][token].lockedAmount;
    }

    function liquidateCollateral(
        address borrower,
        address token,
        uint256 amount,
        address liquidator
    ) external override onlyAuthorized {
        CollateralInfo storage info = userCollateral[borrower][token];
        require(info.lockedAmount >= amount, "Insufficient locked collateral");

        info.amount -= amount;
        info.lockedAmount -= amount;
        info.lastUpdateTime = block.timestamp;

        // Transfer collateral to liquidator with liquidation bonus
        uint256 liquidationBonus = (amount * LIQUIDATION_BONUS) / BASIS_POINTS;
        uint256 totalTransfer = amount + liquidationBonus;

        // Ensure we have enough balance for the bonus
        if (info.amount >= liquidationBonus) {
            info.amount -= liquidationBonus;
        } else {
            totalTransfer = amount + info.amount;
            info.amount = 0;
        }

        IERC20(token).safeTransfer(liquidator, totalTransfer);

        emit CollateralLiquidated(borrower, token, amount, liquidator);
    }

    function swapCollateral(
        address fromToken,
        address toToken,
        uint256 amount,
        uint256 minAmountOut
    ) external nonReentrant whenNotPaused {
        require(supportedCollateral[fromToken] && supportedCollateral[toToken], "Unsupported tokens");

        CollateralInfo storage fromInfo = userCollateral[msg.sender][fromToken];
        require(fromInfo.amount - fromInfo.lockedAmount >= amount, "Insufficient available collateral");

        // Execute swap through DEX integration
        uint256 amountOut = _executeSwap(fromToken, toToken, amount, minAmountOut);

        // Update collateral balances
        fromInfo.amount -= amount;
        userCollateral[msg.sender][toToken].amount += amountOut;

        fromInfo.lastUpdateTime = block.timestamp;
        userCollateral[msg.sender][toToken].lastUpdateTime = block.timestamp;

        emit CollateralSwapped(msg.sender, fromToken, toToken, amount, amountOut);
    }

    function _executeSwap(
        address fromToken,
        address toToken,
        uint256 amount,
        uint256 minAmountOut
    ) internal returns (uint256 amountOut) {
        // Placeholder for DEX integration (Uniswap V3, etc.)
        // In production, this would integrate with a DEX router

        // For now, simulate swap using price feeds
        uint256 fromPrice = priceFeed.getPrice(fromToken);
        uint256 toPrice = priceFeed.getPrice(toToken);
        uint256 fromDecimals = IERC20Metadata(fromToken).decimals();
        uint256 toDecimals = IERC20Metadata(toToken).decimals();

        amountOut = (amount * fromPrice * (10 ** toDecimals)) / (toPrice * (10 ** fromDecimals));

        // Apply 0.3% swap fee
        amountOut = (amountOut * 9970) / 10000;

        require(amountOut >= minAmountOut, "Insufficient output amount");

        return amountOut;
    }

    function getSupportedTokens() external view returns (address[] memory) {
        return supportedTokensList;
    }

    function getCollateralInfo(address user, address token) external view returns (CollateralInfo memory) {
        return userCollateral[user][token];
    }

    function calculateLiquidationValue(address user, address token) external view returns (uint256) {
        CollateralInfo memory info = userCollateral[user][token];
        uint256 price = priceFeed.getPrice(token);
        uint256 decimals = IERC20Metadata(token).decimals();

        uint256 baseValue = (info.lockedAmount * price) / (10 ** decimals);
        uint256 threshold = liquidationThresholds[token];

        return (baseValue * threshold) / BASIS_POINTS;
    }

    // Admin functions
    function addSupportedCollateral(
        address token,
        uint256 collateralFactor,
        uint256 liquidationThreshold
    ) external onlyOwner {
        require(!supportedCollateral[token], "Already supported");
        require(collateralFactor <= BASIS_POINTS, "Invalid collateral factor");
        require(liquidationThreshold <= BASIS_POINTS, "Invalid liquidation threshold");

        supportedCollateral[token] = true;
        collateralFactors[token] = collateralFactor;
        liquidationThresholds[token] = liquidationThreshold;
        supportedTokensList.push(token);
    }

    function removeSupportedCollateral(address token) external onlyOwner {
        require(supportedCollateral[token], "Not supported");

        supportedCollateral[token] = false;
        delete collateralFactors[token];
        delete liquidationThresholds[token];

        // Remove from array
        for (uint256 i = 0; i < supportedTokensList.length; i++) {
            if (supportedTokensList[i] == token) {
                supportedTokensList[i] = supportedTokensList[supportedTokensList.length - 1];
                supportedTokensList.pop();
                break;
            }
        }
    }

    function updateCollateralFactor(address token, uint256 newFactor) external onlyOwner {
        require(supportedCollateral[token], "Not supported");
        require(newFactor <= BASIS_POINTS, "Invalid factor");
        collateralFactors[token] = newFactor;
    }

    function updateLiquidationThreshold(address token, uint256 newThreshold) external onlyOwner {
        require(supportedCollateral[token], "Not supported");
        require(newThreshold <= BASIS_POINTS, "Invalid threshold");
        liquidationThresholds[token] = newThreshold;
    }

    function updatePriceFeed(address _priceFeed) external onlyOwner {
        priceFeed = IPriceFeedAggregator(_priceFeed);
    }

    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }
}

contracts/CreditAggregator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/ICreditAggregator.sol";
import "./interfaces/ICollateralManager.sol";
import "./interfaces/ICreditScoringOracle.sol";
import "./interfaces/IRiskManager.sol";
import "./base/CreditProtocolBase.sol";

/**
 * @title CreditAggregator
 * @dev Main contract for aggregating credit offers and managing credit lifecycle
 */
contract CreditAggregator is CreditProtocolBase, ICreditAggregator {

    struct CreditPosition {
        address borrower;
        uint256 principal;
        uint256 interestRate;
        uint256 collateralAmount;
        address collateralToken;
        uint256 startTime;
        uint256 duration;
        CreditStatus status;
        address lendingProtocol;
    }

    enum CreditStatus {
        Active,
        Repaid,
        Defaulted,
        Liquidated
    }

    mapping(uint256 => CreditPosition) public creditPositions;
    mapping(address => uint256[]) public userCredits;
    mapping(address => uint256) public protocolWeights;

    uint256 private _creditIdCounter;
    ICollateralManager public collateralManager;
    ICreditScoringOracle public creditScoringOracle;
    IRiskManager public riskManager;

    event CreditRequested(uint256 indexed creditId, address indexed borrower, uint256 amount);
    event CreditRepaid(uint256 indexed creditId, uint256 amount);
    event CreditLiquidated(uint256 indexed creditId, address indexed liquidator);

    constructor(
        address _collateralManager,
        address _creditScoringOracle,
        address _riskManager,
        address _owner
    ) CreditProtocolBase(_owner) {
        collateralManager = ICollateralManager(_collateralManager);
        creditScoringOracle = ICreditScoringOracle(_creditScoringOracle);
        riskManager = IRiskManager(_riskManager);
    }

    function requestCredit(
        uint256 amount,
        address collateral,
        uint256 collateralAmount,
        uint256 duration
    ) external override nonReentrant whenNotPaused returns (uint256 creditId) {
        require(amount > 0, "Invalid amount");
        require(duration > 0, "Invalid duration");

        // Verify credit score and risk assessment
        uint256 creditScore = creditScoringOracle.calculateCreditScore(msg.sender);
        require(creditScore >= riskManager.getMinimumCreditScore(), "Insufficient credit score");

        // Lock collateral
        collateralManager.lockCollateral(msg.sender, collateral, collateralAmount);

        // Find optimal lending protocol
        address optimalProtocol = _findOptimalProtocol(amount, duration);
        uint256 interestRate = _calculateInterestRate(msg.sender, amount, creditScore);

        creditId = ++_creditIdCounter;

        creditPositions[creditId] = CreditPosition({
            borrower: msg.sender,
            principal: amount,
            interestRate: interestRate,
            collateralAmount: collateralAmount,
            collateralToken: collateral,
            startTime: block.timestamp,
            duration: duration,
            status: CreditStatus.Active,
            lendingProtocol: optimalProtocol
        });

        userCredits[msg.sender].push(creditId);

        emit CreditRequested(creditId, msg.sender, amount);

        return creditId;
    }

    function repayCredit(uint256 creditId, uint256 amount) external override nonReentrant {
        CreditPosition storage position = creditPositions[creditId];
        require(position.borrower == msg.sender, "Not borrower");
        require(position.status == CreditStatus.Active, "Credit not active");

        uint256 totalOwed = _calculateTotalOwed(creditId);
        require(amount >= totalOwed, "Insufficient repayment amount");

        // Process repayment
        position.status = CreditStatus.Repaid;

        // Release collateral
        collateralManager.releaseCollateral(
            msg.sender, 
            position.collateralToken, 
            position.collateralAmount
        );

        // Update credit score
        creditScoringOracle.updateCreditMetrics(
            msg.sender, 
            ICreditScoringOracle.CreditUpdateType.OnTimePayment, 
            amount
        );

        emit CreditRepaid(creditId, amount);
    }

    function liquidate(uint256 creditId) external override nonReentrant {
        CreditPosition storage position = creditPositions[creditId];
        require(position.status == CreditStatus.Active, "Credit not active");
        require(_isLiquidatable(creditId), "Position not liquidatable");

        position.status = CreditStatus.Liquidated;

        // Execute liquidation through collateral manager
        collateralManager.liquidateCollateral(
            position.borrower,
            position.collateralToken,
            position.collateralAmount,
            msg.sender
        );

        // Update credit score negatively
        creditScoringOracle.updateCreditMetrics(
            position.borrower,
            ICreditScoringOracle.CreditUpdateType.Default,
            position.principal
        );

        emit CreditLiquidated(creditId, msg.sender);
    }

    function getUserCredits(address user) external view returns (uint256[] memory) {
        return userCredits[user];
    }

    function getCreditPosition(uint256 creditId) external view returns (CreditPosition memory) {
        return creditPositions[creditId];
    }

    function _findOptimalProtocol(uint256 amount, uint256 duration) internal view returns (address) {
        // Implementation for finding optimal lending protocol
        // Based on interest rates, liquidity, and protocol weights
        // This would integrate with protocol adapters
        return address(0); // Placeholder
    }

    function _calculateInterestRate(address borrower, uint256 amount, uint256 creditScore) internal view returns (uint256) {
        // Dynamic interest rate calculation based on credit score and market conditions
        // Base rate + risk premium based on credit score
        uint256 baseRate = 500; // 5% in basis points
        uint256 riskPremium = creditScore > 700 ? 0 : (700 - creditScore) * 10; // Additional risk
        return baseRate + riskPremium;
    }

    function _calculateTotalOwed(uint256 creditId) internal view returns (uint256) {
        CreditPosition memory position = creditPositions[creditId];
        uint256 timeElapsed = block.timestamp - position.startTime;
        uint256 interest = (position.principal * position.interestRate * timeElapsed) / (365 days * 10000);
        return position.principal + interest;
    }

    function _isLiquidatable(uint256 creditId) internal view returns (bool) {
        CreditPosition memory position = creditPositions[creditId];

        // Check if loan is overdue
        bool isOverdue = block.timestamp > position.startTime + position.duration;

        // Check collateral ratio
        uint256 collateralValue = collateralManager.getCollateralValue(position.borrower);
        uint256 totalOwed = _calculateTotalOwed(creditId);
        uint256 collateralRatio = (collateralValue * 10000) / totalOwed;

        // Liquidatable if overdue or under-collateralized
        return isOverdue || collateralRatio < 12000; // 120% threshold
    }

    // Admin functions
    function setProtocolWeight(address protocol, uint256 weight) external onlyOwner {
        protocolWeights[protocol] = weight;
    }

    function updateCollateralManager(address _collateralManager) external onlyOwner {
        collateralManager = ICollateralManager(_collateralManager);
    }

    function updateCreditScoringOracle(address _creditScoringOracle) external onlyOwner {
        creditScoringOracle = ICreditScoringOracle(_creditScoringOracle);
    }

    function updateRiskManager(address _riskManager) external onlyOwner {
        riskManager = IRiskManager(_riskManager);
    }
}

contracts/CreditScoringOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./interfaces/ICreditScoringOracle.sol";

/**
 * @title CreditScoringOracle
 * @dev Advanced credit scoring system with comprehensive metrics tracking
 */
contract CreditScoringOracle is ICreditScoringOracle, Ownable, Pausable {

    mapping(address => CreditData) public creditHistory;
    mapping(address => bool) public authorizedUpdaters;
    mapping(address => uint256) public userFirstBorrowTime;
    mapping(address => uint256) public userTotalCreditLimit;

    uint256 public constant MAX_SCORE = 1000;
    uint256 public constant MIN_SCORE = 100;
    uint256 public constant INITIAL_SCORE = 500;

    // Scoring weights (basis points)
    uint256 public constant PAYMENT_HISTORY_WEIGHT = 4000; // 40%
    uint256 public constant UTILIZATION_WEIGHT = 3000;     // 30%
    uint256 public constant HISTORY_LENGTH_WEIGHT = 1500;  // 15%
    uint256 public constant CREDIT_MIX_WEIGHT = 1000;      // 10%
    uint256 public constant DEFAULT_PENALTY_WEIGHT = 500;  // 5%

    event CreditScoreUpdated(address indexed user, uint256 oldScore, uint256 newScore);
    event CreditMetricsUpdated(address indexed user, CreditUpdateType updateType, uint256 value);
    event AuthorizedUpdaterAdded(address indexed updater);
    event AuthorizedUpdaterRemoved(address indexed updater);

    modifier onlyAuthorizedUpdater() {
        require(authorizedUpdaters[msg.sender], "Not authorized");
        _;
    }

    constructor(address _owner) Ownable(_owner) {
        // Initialize with contract deployer as authorized updater
        authorizedUpdaters[msg.sender] = true;
    }

    function getCreditData(address user) external view override returns (CreditData memory) {
        return creditHistory[user];
    }

    function updateCreditMetrics(
        address user,
        CreditUpdateType updateType,
        uint256 value
    ) external override onlyAuthorizedUpdater whenNotPaused {
        CreditData storage data = creditHistory[user];

        // Initialize if first interaction
        if (data.lastUpdateBlock == 0) {
            data.score = INITIAL_SCORE;
            userFirstBorrowTime[user] = block.timestamp;
        }

        uint256 oldScore = data.score;

        if (updateType == CreditUpdateType.OnTimePayment) {
            data.onTimePayments++;
            data.totalRepaid += value;
            _increaseScore(data, 10); // +10 points for on-time payment
        } else if (updateType == CreditUpdateType.LatePayment) {
            data.totalRepaid += value;
            _decreaseScore(data, 20); // -20 points for late payment
        } else if (updateType == CreditUpdateType.Default) {
            data.defaultCount++;
            _decreaseScore(data, 100); // -100 points for default
        } else if (updateType == CreditUpdateType.NewBorrow) {
            data.totalBorrowed += value;
            _updateUtilizationRate(user, data);
        } else if (updateType == CreditUpdateType.Repayment) {
            data.totalRepaid += value;
            _updateUtilizationRate(user, data);
        }

        data.lastUpdateBlock = block.number;

        // Recalculate comprehensive score
        data.score = calculateCreditScore(user);

        emit CreditMetricsUpdated(user, updateType, value);
        emit CreditScoreUpdated(user, oldScore, data.score);
    }

    function calculateCreditScore(address user) public view override returns (uint256) {
        CreditData memory data = creditHistory[user];

        if (data.lastUpdateBlock == 0) {
            return INITIAL_SCORE;
        }

        uint256 score = 0;

        // Payment history (40% weight)
        score += _calculatePaymentHistoryScore(data);

        // Credit utilization (30% weight)
        score += _calculateUtilizationScore(data);

        // Credit history length (15% weight)
        score += _calculateHistoryLengthScore(user);

        // Credit mix/amount (10% weight)
        score += _calculateCreditMixScore(data);

        // Apply default penalty (5% weight)
        score = _applyDefaultPenalty(data, score);

        // Ensure score is within bounds
        if (score > MAX_SCORE) score = MAX_SCORE;
        if (score < MIN_SCORE) score = MIN_SCORE;

        return score;
    }

    function _calculatePaymentHistoryScore(CreditData memory data) internal pure returns (uint256) {
        if (data.onTimePayments + data.defaultCount == 0) {
            return (INITIAL_SCORE * PAYMENT_HISTORY_WEIGHT) / 10000;
        }

        uint256 totalPayments = data.onTimePayments + data.defaultCount;
        uint256 paymentRatio = (data.onTimePayments * 100) / totalPayments;

        return (paymentRatio * PAYMENT_HISTORY_WEIGHT) / 100;
    }

    function _calculateUtilizationScore(CreditData memory data) internal pure returns (uint256) {
        if (data.utilizationRate <= 30) {
            return UTILIZATION_WEIGHT; // Full points for low utilization
        } else if (data.utilizationRate <= 70) {
            uint256 penalty = ((data.utilizationRate - 30) * UTILIZATION_WEIGHT) / 40;
            return UTILIZATION_WEIGHT - penalty;
        }
        return 0; // No points for high utilization
    }

    function _calculateHistoryLengthScore(address user) internal view returns (uint256) {
        if (userFirstBorrowTime[user] == 0) {
            return 0;
        }

        uint256 historyMonths = (block.timestamp - userFirstBorrowTime[user]) / 30 days;
        if (historyMonths == 0) {
            return 0;
        }

        // Maximum score at 24 months
        uint256 historyScore = historyMonths > 24 ? HISTORY_LENGTH_WEIGHT : (historyMonths * HISTORY_LENGTH_WEIGHT) / 24;
        return historyScore;
    }

    function _calculateCreditMixScore(CreditData memory data) internal pure returns (uint256) {
        if (data.totalBorrowed == 0) {
            return 0;
        }

        // Rewards larger borrowers up to 100,000 tokens
        uint256 borrowScore = data.totalBorrowed > 100000e18 ? 
            CREDIT_MIX_WEIGHT : 
            (data.totalBorrowed * CREDIT_MIX_WEIGHT) / 100000e18;

        return borrowScore;
    }

    function _applyDefaultPenalty(CreditData memory data, uint256 currentScore) internal pure returns (uint256) {
        if (data.defaultCount == 0) {
            return currentScore;
        }

        uint256 defaultPenalty = data.defaultCount * (DEFAULT_PENALTY_WEIGHT / 10); // 50 points per default
        return currentScore > defaultPenalty ? currentScore - defaultPenalty : MIN_SCORE;
    }

    function _increaseScore(CreditData storage data, uint256 points) internal {
        data.score = data.score + points > MAX_SCORE ? MAX_SCORE : data.score + points;
    }

    function _decreaseScore(CreditData storage data, uint256 points) internal {
        data.score = data.score > points ? data.score - points : MIN_SCORE;
    }

    function _updateUtilizationRate(address user, CreditData storage data) internal {
        // Calculate current utilization based on active positions
        uint256 totalLimit = userTotalCreditLimit[user];
        if (totalLimit == 0) {
            data.utilizationRate = 0;
            return;
        }

        uint256 currentDebt = data.totalBorrowed - data.totalRepaid;
        data.utilizationRate = (currentDebt * 100) / totalLimit;
    }

    // View functions
    function getUserCreditSummary(address user) external view returns (
        uint256 score,
        uint256 totalBorrowed,
        uint256 totalRepaid,
        uint256 onTimePayments,
        uint256 defaultCount,
        uint256 utilizationRate,
        uint256 accountAge
    ) {
        CreditData memory data = creditHistory[user];
        uint256 accountAge = userFirstBorrowTime[user] > 0 ? 
            (block.timestamp - userFirstBorrowTime[user]) / 1 days : 0;

        return (
            data.score,
            data.totalBorrowed,
            data.totalRepaid,
            data.onTimePayments,
            data.defaultCount,
            data.utilizationRate,
            accountAge
        );
    }

    function getScoreBreakdown(address user) external view returns (
        uint256 paymentHistoryScore,
        uint256 utilizationScore,
        uint256 historyLengthScore,
        uint256 creditMixScore,
        uint256 totalScore
    ) {
        CreditData memory data = creditHistory[user];

        paymentHistoryScore = _calculatePaymentHistoryScore(data);
        utilizationScore = _calculateUtilizationScore(data);
        historyLengthScore = _calculateHistoryLengthScore(user);
        creditMixScore = _calculateCreditMixScore(data);
        totalScore = calculateCreditScore(user);

        return (paymentHistoryScore, utilizationScore, historyLengthScore, creditMixScore, totalScore);
    }

    function isEligibleForCredit(address user, uint256 minimumScore) external view returns (bool) {
        return calculateCreditScore(user) >= minimumScore;
    }

    // Admin functions
    function addAuthorizedUpdater(address updater) external onlyOwner {
        require(updater != address(0), "Invalid address");
        authorizedUpdaters[updater] = true;
        emit AuthorizedUpdaterAdded(updater);
    }

    function removeAuthorizedUpdater(address updater) external onlyOwner {
        authorizedUpdaters[updater] = false;
        emit AuthorizedUpdaterRemoved(updater);
    }

    function setCreditLimit(address user, uint256 limit) external onlyAuthorizedUpdater {
        userTotalCreditLimit[user] = limit;
    }

    function batchUpdateScores(address[] calldata users) external onlyOwner {
        for (uint256 i = 0; i < users.length; i++) {
            uint256 oldScore = creditHistory[users[i]].score;
            uint256 newScore = calculateCreditScore(users[i]);
            creditHistory[users[i]].score = newScore;
            emit CreditScoreUpdated(users[i], oldScore, newScore);
        }
    }

    function initializeUser(
        address user,
        uint256 initialScore,
        uint256 totalBorrowed,
        uint256 totalRepaid,
        uint256 onTimePayments,
        uint256 defaultCount
    ) external onlyOwner {
        require(creditHistory[user].lastUpdateBlock == 0, "User already initialized");

        creditHistory[user] = CreditData({
            score: initialScore,
            totalBorrowed: totalBorrowed,
            totalRepaid: totalRepaid,
            defaultCount: defaultCount,
            onTimePayments: onTimePayments,
            utilizationRate: 0,
            lastUpdateBlock: block.number
        });

        userFirstBorrowTime[user] = block.timestamp;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function emergencyResetScore(address user, uint256 newScore) external onlyOwner {
        require(newScore >= MIN_SCORE && newScore <= MAX_SCORE, "Invalid score");
        uint256 oldScore = creditHistory[user].score;
        creditHistory[user].score = newScore;
        emit CreditScoreUpdated(user, oldScore, newScore);
    }
}

contracts/Greeter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Greeter {
    string private greeting;

    constructor(string memory _greeting) {
        greeting = _greeting;
    }

    function greet() public view returns (string memory) {
        return greeting;
    }

    function setGreeting(string memory _greeting) public {
        greeting = _greeting;
    }
}

