// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title ICreditAggregator
 * @dev Interface for the main credit aggregation contract
 */
interface ICreditAggregator {
    function requestCredit(
        uint256 amount,
        address collateral,
        uint256 collateralAmount,
        uint256 duration
    ) external returns (uint256 creditId);

    function repayCredit(uint256 creditId, uint256 amount) external;

    function liquidate(uint256 creditId) external;

    function getUserCredits(address user) external view returns (uint256[] memory);
}

contracts/interfaces/ICreditScoringOracl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title ICreditScoringOracle
 * @dev Interface for credit scoring and metrics tracking
 */
interface ICreditScoringOracle {
    struct CreditData {
        uint256 score;           // 0-1000 scale
        uint256 totalBorrowed;
        uint256 totalRepaid;
        uint256 defaultCount;
        uint256 onTimePayments;
        uint256 utilizationRate;
        uint256 lastUpdateBlock;
    }

    enum CreditUpdateType {
        OnTimePayment,
        LatePayment,
        Default,
        NewBorrow,
        Repayment
    }

    function getCreditData(address user) external view returns (CreditData memory);

    function updateCreditMetrics(
        address user, 
        CreditUpdateType updateType, 
        uint256 value
    ) external;

    function calculateCreditScore(address user) external view returns (uint256);
}

